/*
Variation exercise on example find on :
https://www.youtube.com/watch?v=l-07BXzNdPw&ab_channel=TheArtofCode
*/

vec2 N22(vec2 p){
    vec3 a = fract(p.xyx*vec3(123.45,6.789,0.154));
    a += dot(a, a+26.45);
    return fract(vec2(a.x*a.y,a.y*a.z));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    uv *= .7;
    float m = 0.;    
    float t = iTime*.3;

    float minDist = 100.;

    for(float i=1.; i<30.; i++){
        vec2 n = N22(vec2(i));
        vec2 p = sin(n*t);
        float d = length(uv - p);
        m += smoothstep(.015,.01,d);   // dots
        if(d<minDist){
            minDist = d;
        }        
    }    

    vec3 col = vec3(0);

    if(false){
        col = vec3(minDist*1.5-.0,.0,.0) + m*.7;    
    }else{
        col = vec3(1.-minDist*1.7,(minDist)*0.3,.0) + m;
    }

    fragColor = vec4(col,1.0);
}
