#define SPEED 12.0

#define RAYS 12.0

#define RING_PERIOD 129.0

#define TWIST_FACTOR 0.72

float getColorComponent(float dist, float angle) {
    return
        pow((
            (
            	cos(
                    (angle * RAYS)
            		+ pow(
                        dist * 0.79,
                		(sin(iTime * SPEED) * TWIST_FACTOR)
            		) * 3.0
        		) + sin(
            		dist * RING_PERIOD
        		)
        	) + 1.6
        ) / 2.0, 3.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 delta = (fragCoord -.5 * iResolution.xy) / iResolution.y;
    float dist = length(delta),
    angle = atan(delta.x, delta.y);
    fragColor = vec4(
        getColorComponent(dist, angle),
        getColorComponent(dist * 0.0005, angle),        
        getColorComponent(dist * 0.07, angle),
        5.0
    );
}
