float my_max4(in vec4 vector)
{
    return max(max(vector.x,vector.y),vector.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Output to screen

    vec4 my_texture_0 = texture(iChannel0, (fragCoord.xy-vec2(iTime*10.0,0))/iResolution.xy);
    vec4 my_video = texture(iChannel1, fragCoord.xy/iResolution.xy);

    //Find if green
    if (my_max4(abs(my_video - vec4(0.051,0.639,0.145,1.0))) <= 0.2)
    {
        // es ist grun
        fragColor = my_texture_0;
    }
    else
    {
        // es ist nicht grun
        fragColor = my_video;
    }

}
