float fun(float x, float p) {
    if(x <= 0.5){
        return 0.5 * pow(2.0 * x, p);
    } else {
        return 1.0 - 0.5 * pow((2.0 * (1.0 - x)), p);    
    }
}


float beiz(float u, float c1, float c2, float c3, float c4) {
    return pow(1.0 - u, 3.0) * c1 + 3.0 * u * pow(1.0 - u, 2.0) * c2 + 3.0 * pow(u, 2.0) * (1.0 - u) * c3 + pow(u, 3.0) * c4;
}

float vBase(float y, float f1, float f2) { return (y - f2) / (f1 - f2); }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
