    float fu1 = beiz(uv.x, 0.83 + sin(iTime), 0.09 + sin(iTime), 0.13+ cos(iTime), .05);
    float fu2 = beiz(uv.x, 0.99+ sin(iTime), 0.85 + sin(iTime), 0.88 + cos(iTime), .19);


    fragColor = vec4(bl, 1.0);


    vec4 ret1 = vec4(vec3(0.0), 1.0);
    vec4 ret2 = vec4(vec3(0.0), 1.0);

    if(fu1 < uv.y && fu2 > uv.y) {
        fragColor = vec4(col * (vBase(uv.y, fu1, fu2) * abs(1.1 + cos(iTime)/2.0)) , 1.0);
    } else {
        fragColor = vec4(1.0 + sin(iTime)/2.0, 1.0 - sin(uv.x), distance(sin(iTime), 0.5), 1.0);
    }
}
