void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float f = 40. * 0.05;
    float cf = 1. / f;

    float p = SinMod(iTime, cf, 0., 1.);
    float s = SinMod(iTime, p, 0., 1.);

    vec3 modCol = sin(s) * vec3(0,2,4);

    vec2 uv = fragCoord / (iResolution.xy * 0.1);
    vec3 col = cos(s+uv.xyx + modCol);

    fragColor = vec4(col,1.0);
}

float Amp(float a)
{
    return a * 0.1;
}

float SinWave(float f, float a, float t)
{
    return Amp(a) * sin(2. * PI * f * t);
}

vec2 mainSound(in int samp, float t)
{
    float f = 40.;
    float cf = 1. / f;

    float p = SinMod(t, cf, 0., 1.);
    float s = SinWave(p, 0.3, t);

    return vec2(s);
}

#define PI 3.1415926536

float SinMod(float x, float b, float c, float d)
{
    float p = (2. * PI) / b;
    return sin(p * (x + c)) + d;
}
