#define TAU 6.2831853071
#define quarter TAU/4.
#define green1 vec3(51., 115., 87.)
#define green2 vec3(109., 159., 113.)
#define bg vec3(228., 227., 211.)

mat2 rot(in float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

float square(in vec2 st, in vec2 center, 
    in float thiccness){
    st *= rot(quarter/2.);
    return step(0.5, 
    thiccness-abs(dot(st-center, vec2(1., 0.)*100.))+
    thiccness-abs(dot(st-center, vec2(0., 1.)*100.))
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec2 uv2 = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    uv *= rot(quarter/2.);
    uv *= rot( //breeze uwu
    sin(iTime*0.7)*
    sin(iTime*0.9)*
    sin(iTime*1.1)*
    sin(iTime*1.3)*
    0.03);
    uv *= 0.2;
    uv = abs(uv);

    float size = 5.;
    float brightness = square(uv, vec2(0.), size);

    for(float i=0.; i<6.; i++){
        uv-=0.1;
        uv = abs(uv);
        uv*=2.;
        brightness += square(uv, vec2(0.), size);
    }

    vec3 col = vec3(step(1.0, brightness));
    col = mix(bg, mix(green1, green2, length(uv2)), col.r);
    col/=255.;

    fragColor = vec4(col,1.0);
}
