vec2 getChar(vec2 uv, int index)
{
    return (uv/16.0)+vec2(mod(float(index), 16.)*0.0625, floor(float(index)/16.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    int index = int(floor(iTime));

    int string[10];
    string = int[](1,2,1,3,1,4,1,5,1,6);

    int charIndex = int(floor(uv.x*16.)+floor(uv.y*16.)*16.);
    vec4 a = texture(iChannel0, getChar(uv,string[charIndex])*16.);

    fragColor = vec4(a);
}
