float xor(float a, float b){
return a*(1.-b)+b*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 mask = vec3(0);
    uv*=6.;
    vec2 gv = fract(uv)-0.5;
    vec2 id = floor(uv);

    //parameters to tweak
    float smoothness = 0.5;
    float xorsmoothness = .1;
    float modTweak  = .6;
    float minRaius = 0.9;
    float maxRadius = 1.5;
    float distortion = 3.7;
    float inOut =-1.; //-1 from center or 1 to center

    float m= 0.;
    float t = iTime;
    for(float y=-1.; y<=1.; y++){
        for(float x=-1.; x<=1.;x++){
            vec2 offs = vec2(x,y);
            float d= length(gv-offs);
            float dist = length(id+offs)*distortion;
            float r = mix(minRaius,maxRadius,sin(inOut*t+dist)*0.5+.5);
            m+=smoothstep(r,r*smoothness ,d);
            m=xor(m,smoothstep(r,r*xorsmoothness ,d));
        }
    }


    mask+=mod(m,modTweak);

    fragColor = vec4(mask,1.0);
}
