void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   float value, fft, wave;
    value = abs(uv.x-0.5);

    // first texture row is frequency data
	fft  = texture( iChannel0, vec2(value,0.75) ).x; 

  	// second texture row is the sound wave
	wave = texture( iChannel0, vec2(value,0.95) ).x;

    // Time varying pixel color
    vec3 col = vec3( 4.0*fft*(1.0-fft)*(1.2+0.4*sin(iTime))
                    , fft+1.3*sin(0.22*iTime)
                    , 1.5-fft-0.4*sin(0.33*iTime) ) * fft;

    // Output to screen
    fragColor = vec4(col,5.0);
}
