void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float s = 16.0;
    // Time varying pixel color
    vec3 col = 4.0*cos(iTime+uv.xyx*vec3(s*sin(iTime*0.01),s*cos(iTime*0.02),s*cos(uv.x+iTime*0.06)));
       col = abs(col);
    // Output to screen
    fragColor = vec4(vec3(col.x*col.y*col.z),1.0);
}
