vec4 rect (vec2 uv , float xc, float yc, float w, float h){


    float x = uv.x;
    float y = uv.y;

    h *= 1.77;

    if ( (xc-w/2.0 <= x && x <= xc+w/2.0) &&   (yc-h/2.0 <= y && y <= yc+h/2.0)  ){

        float ir = x;
        float ig = y;
        float ib = 0.0;

        // Output to screen
        return vec4(ir,ig,ib,1.0);

    }


    return vec4 (0.0) ;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float x = uv.x;
    float y = uv.y; 
    float t = iTime;


    // Output to screen
    fragColor = vec4 (y*0.5 , x*0.5 , x+y*0.1 ,0.0 ) + rect (uv, 0.2*cos(t-sin(t)) + 0.5 , 0.2*sin(t) + 0.5 , 0.16,0.16);

    return;
}
