#define S(v) smoothstep(1.5/R.y,0.,v) // AA
#define r(x) fract(sin(x*91.2228)*412.5453) // hash
void mainImage( out vec4 O, in vec2 u )
{
    vec2 R = iResolution.xy, U = (u - R * .5) / R.y;
    vec3 col = vec3(.7, .9, 1.);
    for(float i = 1.; i < 9.; i++)
        col = mix(col,mix(col,col*.85,.1*i), S(U.y-.1+.032*i+abs(U.x+(r(i*.2)-.5)*.3)*.1));
    O = vec4(mix(vec3(.05),col,S(length(U)-.2)),1.0);
}
