#define PixelSize 32.

vec2 rotate2D(vec2 _st,float _angle){
    _st=mat2(cos(_angle),-sin(_angle),
    sin(_angle),cos(_angle))*_st;
    return _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - iResolution.xy / 2.0) / iResolution.y;
    uv = ceil(uv*PixelSize)/PixelSize; //pixelise

    vec2 uv1 = rotate2D(uv, (length(uv*5.)+iTime)*2. );
    uv += vec2(0.,0.025);
    vec2 uv2 = rotate2D(uv, (length(uv*5.)+iTime)*2. );


    vec3 col1 = vec3(0.97,0.5,0.74);
    vec3 col2 = vec3(0.03);
    vec3 col3 = mix(col1,col2,0.8);
    col2 = mix(col3,col2,float((uv2.x) > 0.05));

    vec3 col = mix(col1,col2,float((uv1.x) > 0.));

    fragColor = vec4(col,1.0);
}
