void mainImage( out vec4 O, vec2 u )
{
    O = texelFetch(iChannel0, ivec2(u), 0 );
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = (2.*u - R ) / R.y;
    float x = U.x;
    U.x -= .8*sign(x);                      // offset for the 2 views
    float t = x<0. ? iTime                  // left use iTime, assumed 60 fps
                   : float(iFrame)/60.,     // right use iFrame
          a = mod( atan(U.y,U.x) - t , 6.283 ) -3.1415;   // rotating angle
    O =  max(  pow(vec4(.9,.8,.7,0),vec4(.05))            // fade past color
             * texelFetch(iChannel0, ivec2(u), 0 ),
             vec4( step( abs(a) - .5/60. , 0. ) )         // draw new fan segment
         //  vec4( smoothstep( 3./R.y/length(U), 0., abs(a) - .5/60. ) )
            );
}
