void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord -.5 * iResolution.y) / iResolution.y;
    float id = length(uv)*5.;
    uv *= 15.;

    uv = fract(uv)-.5;

    float speed = 5.;
    float radius = .4;

    vec2 pos = uv+vec2(sin(iTime*speed+id)*radius, cos(iTime*speed+id) * radius);

    vec3 col = 1.-step(.05, vec3(length(pos)));

    //if(uv.x > .45 || uv.y > .45) col = vec3(1., 0., 0.);

    fragColor = vec4(col, 1.);
}
