#define centerX iResolution.x * 0.5
#define centerY iResolution.y * 0.5

#define START iResolution.y * 0.3
#define INTERVAL iResolution.y * 0.1
#define R 100.0

#define MOVEINTENSITY 0.4

// Negative distance.
float negdst(float x0, float y0, float x1, float y1, float m){
    return 1.0 - pow(pow(x0-x1,2.0)+pow(y0-y1,2.0),0.5)/m;
}

float curve(float t){
    return sin(t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    fragColor = vec4(
        negdst(fragCoord.x, fragCoord.y, centerX * (1.0 + MOVEINTENSITY * curve(iTime * 1.0)), (START + 0.0 * INTERVAL) * curve(iTime) + centerY, R),
        negdst(fragCoord.x, fragCoord.y, centerX * (1.0 + MOVEINTENSITY * curve(iTime * 1.5)), (START + 1.0 * INTERVAL) * curve(iTime) + centerY, R),
        negdst(fragCoord.x, fragCoord.y, centerX * (1.0 + MOVEINTENSITY * curve(iTime * 2.0)), (START + 2.0 * INTERVAL) * curve(iTime) + centerY, R),
        1.0
    );
}
