#define S(v) smoothstep(-.8,.8, (v-1.)/fwidth(v) )

void mainImage( out vec4 O, vec2 C )
{
    vec2 uv = C/iResolution.xy;
    O = mix( vec4(.137, .231, .262, 1),
             vec4(.6,   .819, .803, 1),
             S( texture(iChannel0,uv).a ));
}

void mainImage( out vec4 O, in vec2 C )
{
    vec2 uv = C/iResolution.xy;
    uv = (1.-ZOOM_SPEED)*(uv-vec2(.5,.5))+vec2(.5,.5);
    O = fast_gaussian_blur(iChannel0,uv,iResolution.xy,vec2(1.0,0.0),BLUR_A);
    if(iMouse.z>.5)
    {
        if(length(C-iMouse.xy)<10.)
        O = vec4(1.0);
    }
    if(length(C-iResolution.xy/2.)<10.)
        O = vec4(1.0);
    if(iFrame<2)
    {
        O = vec4(0.0);
    }
}

#define BLUR_A 2.
#define BLUR_B 8.
#define ZOOM_SPEED .08


const float inv_sqrt_2pi = 0.3989422804014327;
float gaussianf(float sigma, float dist)
{
    float a = dist / sigma;
    return (inv_sqrt_2pi / sigma ) * exp( -0.5 * a * a );
}
vec4 fast_gaussian_blur(sampler2D image, vec2 uv,vec2 resolution, vec2 direction, float blur)
{
    vec4 color = vec4(0.0);
    float coef = gaussianf(blur,0.0);
    color += texture(image, uv) * coef;
    float kernel_s = blur * 2.;
    //removed precomputing weights by onehade
    float prev = gaussianf(blur, 0.0);
    for (float i = 0.0; i < kernel_s; i++) {
        float w1 = prev; 
        float w2 = gaussianf(blur, i + 1.0);
        prev = w2; //storing redudant weight for next loop
        float c = w1 + w2;
        vec2 off1 = vec2((i * w1 + (i + 1.0) * w2) / c) * direction / resolution;
        color += texture(image, uv - off1 ) * c;
        color += texture(image, uv + off1 ) * c;
        coef += c + c;
    }

    return color/coef;
}

void mainImage( out vec4 O, in vec2 C )
{
    vec2 uv = C/iResolution.xy;
    O = fast_gaussian_blur(iChannel0,uv,iResolution.xy,vec2(0.0,1.0),BLUR_A);
}

void mainImage( out vec4 O, in vec2 C )
{
    vec2 uv = C/iResolution.xy;
    O = fast_gaussian_blur(iChannel0,uv,iResolution.xy,vec2(1.0,0.0),BLUR_B);
}

void mainImage( out vec4 O, in vec2 C )
{
    vec2 uv = C/iResolution.xy;

    float o =  texture(iChannel1,uv).x-fast_gaussian_blur(iChannel0,uv,iResolution.xy,vec2(0.0,1.0),BLUR_B).x;
    O = vec4(vec3(clamp(o,0.,1.)*300.) , o );
    //O = vec4(vec3(clamp(o,0.,1.)*300.),1.);
}
