void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 oguv = fragCoord / iResolution.xy;

    vec2 fc = fragCoord;

    fc.x += 10.*sin(iTime);
    fc.y += 10.*cos(iTime);

    vec2 uv = fc / vec2(textureSize(iChannel0, 0));

    vec3 col;
    if (oguv.y > .5)
        col = texture(iChannel0, uv).rgb;
    else
        col = texture(iChannel1, uv).rgb;

    if (abs(oguv.y - .5) <= 1./iResolution.y)
        col = vec3(1);

    fragColor = vec4(col,1.0);
}
