void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= (iResolution.y / iResolution.x);
    uv.y += (1.0 - (iResolution.y / iResolution.x)) / 2.0;

    float x = fract(iTime * 0.5) * 2.0 - 2.0;
    float trajectory = abs(sin(1.6*x*x + 10.0f*x + 1.0f)) * 0.1f * (x*x*x*x);

    float angle = 3.9 - (trajectory);

    vec2 def = vec2(2.0f + sin(angle), 2.0f + cos(angle));
    //def = vec2(1.0f, 2.0f);

    float dis = distance(uv * def, vec2(0.5, 0.4 + trajectory) * def);

    //float dis = distance(uv * def, vec2(0.5, 0.5) * def);

    dis = smoothstep(0.1, 0.105, dis);

    vec3 col = vec3(dis, dis, dis);

    fragColor = vec4(col,1.0);
}
