#define SPEED 4.0
#define THICKNESS 0.01

vec4 edgeColour = vec4(0.7, 0.2, 0.0, 1.0);
vec4 centreColour = vec4(0.8, 0.9, 0.8, 1.0);

vec3 fire(vec2 uv)
{
	uv.y = abs(uv.y - 0.5);

	float noiseTex = texture(iChannel0, vec2(uv.x - iTime * SPEED, uv.y * 10.)).r;
	float xNoise = texture(iChannel0, vec2(uv.x * 0.1 - iTime * SPEED, 0.0)).r;
	float envelope =  1.0 - uv.y / (THICKNESS + xNoise * 0.4);

	envelope = clamp(envelope, 0.0, 1.0);

	float colourInterp = clamp(uv.y / THICKNESS, 0.0, 1.0);
	vec3 currentColour = mix(centreColour.rgb, edgeColour.rgb * 4.0, colourInterp);

	vec3 col = noiseTex * currentColour * envelope;

	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 b = fire(uv);

    fragColor = vec4(b, 1.0);
}
