void mainImage( out vec4 O, vec2 u )
{
    vec2 U = u/R;
    O = T(u);              // red = hot, blue = cold
    O.b = 1.-O.r;
    float v = O.r-U.x;
    O = mix(O, vec4(0,1,0,1), smoothstep(1.5,0., abs(v)/fwidth(v)) ); // temperature curve with height
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 U = u/R;
    O = textureLod(iChannel0, U,floor(log2(R.y)-5.) ); // diffusion
    if ( U.y<.05 ) O.r = .5+.5*sin(iTime); // temperature forcing at floor
}
