#define T(U,l) textureLod( iChannel0, U, float(l) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;
    u /= R;
    O-=O;
    float w, t=0.;
    for( float i=0.; i<8.; i++ ) {
        w = sin(6.28*(iTime-i)/8.);
        t += w = U.x < 0. ? .5+.5*w : w;
        O += w*T(u,i);
     // O += w*( T(u,i) - ( i>0. ? T(u,i-1.) : vec4(0) ) );
    }
    if (U.x<0.) O /= t; else O = .5*O+.5;
}
