vec2 f(vec2 p, vec2 C)
{
    return vec2(p.x * p.x - p.y * p.y + C.x, 2.0 * p.x * p.y + C.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 C = uv * vec2(2.0, 2.0) - vec2(1.5, 1.0);

    vec2 c = vec2(0.0, 0.0);

    for(int i = 0; i < 2000; i++)
        c = f(c, C);

    fragColor = vec4(vec3(length(c) < 2.0 ? 1.0 : 0.0), 1.0);
}
