//camera position
float zoom = .25;
vec2 pos = vec2(0, 0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //analise clicks
    if(iMouse.xy != iMouse.xy) {
        pos += (iMouse.xy -.5* iResolution.xy ) / iResolution.y;
        pos = pos * 4.0;

        zoom *= 2.0;
    }

    //compute colors
    float inf = 16.0;
    vec2 c = ( fragCoord -.5* iResolution.xy ) / iResolution.y + pos*zoom;
    vec2 pixel = vec2(0,0);
    c.x /= zoom;
    c.y /= zoom;
    float maxItter = 100.0;
    bool working = true;
    float i = 0.0;
    while(i < maxItter && working) {
        vec2 temp = vec2(0.0,0.0);

        temp.x = pixel.x * pixel.x - pixel.y * pixel.y + c.x;
        temp.y = pixel.x * pixel.y + pixel.x * pixel.y + c.y;

        pixel = temp;

        if(pixel.x * pixel.x + pixel.y * pixel.y >= inf) {
            working = false;
        }

        i++;
    }

    if(working) {
        fragColor = vec4(0,0,0,1);
    } else {
        fragColor = vec4(sin(i / 10.0), sin(i / 10.0 + 100.0), sin(i / 10.0 + 1000.0), 1);
    }
}
