#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

// smooth min from https://www.cl.cam.ac.uk/teaching/1718/FGraphics/1.%20Ray%20Marching%20and%20Signed%20Distance%20Fields.pdf
float smin(float a, float b) {
float k = 0.5;
float h = clamp(0.5+ 0.5 *(b-a) /k, 0.,1.);

return mix(b,a,h) - k * h *(1. -h);

}

mat2 Rot(float a) {
 float s = sin(a);
 float c = cos(a);
 return mat2(c, -s, s, c);
}

float sdCylinder(vec3 p, vec3 a, vec3 b, float r) {
    vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);
    //t = clamp(t, 0., 1.);

    vec3 c = a + t*ab;
    //distance between point p to point c
    float x = length(p-c)-r;
    float y = (abs(t-.5)-.5)*length(ab);
    // exterior distance
    float e = length(max(vec2(x, y),0.0));
    float i = min(max(x,y),0.);
    return e + i;

}


float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
    vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);
    t = clamp(t, 0., 1.);

    vec3 c = a + t*ab;
    //distance between point p to point c
    return length(p-c)-r;

}

float sdTorus(vec3 p, vec2 r) {
    float x = length(p.xz) - r.x;
    return length(vec2(x, p.y))-r.y;
}

float sdBox(vec3 p, vec3 s) {
    return length(max(abs(p)-s, 0.));
}

float GetDist(vec3 p) {
    float pd = p.y;

    //Torus
    vec3 bp = p; 
    bp -= vec3(0,1,6);
    bp.xz *= Rot(iTime);

    bp.yz *= Rot(iTime);
    float bd = sdTorus(bp, vec2(1,.2));


    //Torus2
    vec3 bp2 = p;
    bp2 -=vec3(1,1,6);
    bp2.xz *= Rot(iTime);
    bp2.yz *= Rot(iTime);
    float bd2 = sdTorus(bp2, vec2(1,.2));

   //sphere
    vec3 sp = p;
    sp -= vec3(1,1,6);
    //sp *= vec3(4,4,4);
    float sd = length(sp) -1.;

    //sphere 2
    vec3 sp2 = p;
    sp2 -= vec3(1,1,6);
    float sd2 = length(sp2) -1.5;


    float unionBoxAndSphere2 = mix(sd2, bd, sin(iTime)*.5+.5);
    float subBoxAndsphere = max(-sd2,unionBoxAndSphere2);

    float d = smin(bd2, pd);
    d = smin(d, unionBoxAndSphere2);
    //d= smin(d, bd2);
    return d;

}

float RayMarch(vec3 ro, vec3 rd) {
    // distance from origin
    float dO=0.;

    // Ray marching loop
    for(int i=0; i<MAX_STEPS; i++) {
        // start at ray origin then march in certain distance in ray direction
        vec3 p = ro + rd *dO;
        float dS = GetDist(p);
        dO += dS;
        //HIT!!
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }
    return dO;
}


vec3 GetNormal(vec3 p) {
    float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
         GetDist(p-e.xyy),
         GetDist(p-e.yxy),
         GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0. , 1.);
    //shadows
    float d = RayMarch(p+n*SURF_DIST*2., l);
    if(d<length(lightPos-p)) dif *= .1;
    return dif;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // ray origin
    vec3 ro = vec3(0, 2, 0);
    // ray direction
    vec3 rd = normalize(vec3(uv.x, uv.y-.2, 1));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    //difuse lighting
    float dif = GetLight(p);
    vec3 col = vec3(.1, dif, .3);


    fragColor = vec4(col,1.0);
}
