const float pi = 3.14159265359;

vec3 colorA = vec3(0.141, 0.141, 0.812);
vec3 colorB = vec3(0.910, 0.633,0.424);

float plot (vec2 st, float pct){
  return  smoothstep( pct-0.01, pct, st.y) -
          smoothstep( pct, pct+0.01, st.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = gl_FragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    vec3 pct = vec3(st.y);

    pct.r = smoothstep(0.0,1.0, (cos(st.x)));
    pct.g = sin(0.235 * st.x * pi * mod(iTime, 4.8) + 00.0);
	pct.b = sin(pi * st.x);

    color = mix(colorA, colorB, pct);

    vec3 pct2 = vec3(st.x);
    pct2.r = sin(mod(42.0 * iTime, st.x + st.y) * (st.x + 5.0 * st.y));
    color = mix(color - vec3(0.2), color + vec3(0.2) , pct2);

    fragColor = vec4(color,1.0);

}
