void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    const float WIDTH = 16.;
    vec2 cell = floor(uv * WIDTH);

    vec3 col = abs(
        vec3(
        cell.x*sin( -iTime*2. + abs(cell.x)*.05 ),
        cell.y*1.1+sin(iTime*4.),
        cell.y)
    ) * (1./WIDTH);

    float result = 0.;
    int val = int(col.x) & int(col.y) % 3;
    for(int i = 0; i < 3; i++){
        col.y -= (.2 - result);
        col *= 1.5;
        val = (int(col.x-col.y) & int(col.y+col.x*4.0)) % 3;
        result = (result + float(val)) * .5;
        col.x = (result*2.0+col.x)/3.0;
        col = col.yzx;
    }  


    fragColor = vec4(col,1.0);
}
