#define SPEED 1.0

#define SCANLINE_SIZE 8.0
#define SCANLINE_SPEED 1

#define WAVE_FREQ 8.0
#define WAVE_SPEED 0.01

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float time = iTime * SPEED;

    float side = (mod(fragCoord.y, SCANLINE_SIZE) <= SCANLINE_SIZE/2.0 ? 1.0 : -1.0);
    float scanline = fragCoord.y / SCANLINE_SIZE;

    float sinOff = sin(time * 1.0 + scanline);

    vec2 offset = vec2(0.0, 0.0);
    offset += vec2(sinOff * WAVE_SPEED * side, 0.0);
    offset += vec2(sin(time + fragCoord.y / WAVE_FREQ + scanline) * 0.01 * side, 0.0);

    offset += vec2(0.0, sinOff * WAVE_SPEED * side);
    offset += vec2(0.0, sin(iTime + fragCoord.x / WAVE_FREQ + scanline) * 0.01 * side);

    vec3 col = texture(iChannel0, uv + offset).rgb;

    fragColor = vec4(col,1.0);
}
