void mainImage( out vec4 O, vec2 u )
{
    vec2 U = u/iResolution.xy;
    O  = vec4(.96,.98,1, 1);         // base color
    O *= mix(.0,1., U.x );           // luminance gradient

    float n = ceil(exp2(8.*U.y));    // emulates 0-8bits with height
    vec4  d = iMouse.z<=0. ? vec4(0) // click 
              : texelFetch(iChannel0,ivec2(u),0) - .5; // dithering
    O = floor( O*n + d )/ n;         // quantized color
}
