void mainImage(out vec4 O, vec2 I)
{
    vec3 a = iResolution,
    p = cos(iTime+a),
    d = a,
    i = a;
    p.x += iTime/.1;

    for(d.yz = a.xy-roundEven(I)*2.;
        i.z++<9e2 && texture(iChannel0, 
        a = ceil(p += min(a = mod(-p,sign(d))/d+3e-8, min(a.y,a.z)).x*d)/3e2).g
        > length(a.yz)/.1;);

    O.rgb = fwidth(p);
}

