#define DTR 0.01745329
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec2 uv;
vec3 cp,cn,cr,ss,oc,gl,vb;
vec4 fc,cc;
float tt,cd,sd,md,io,oa,td,tc;
int es=0,ec;

float smin(float a, float b, float k){float h=clamp(0.5+0.5*(b-a)/k,0.,1.);return mix(b,a,h)-k*h*(1.-h);}

float space(vec3 p)
{
		float v = length(sin(p.xz));
		v -= sin(p.y*cos(tt*0.1)*0.5+0.8+tt*0.5-length(p)*0.2);
		v += dot(sin(p*2.),cos(p.zyx))*0.5;
		return v * 0.3;
}

float mp(vec3 p)
{
		vec3 pp = p;

		p.xz *= rot(tt*0.05);

		sd = space(p+vec3(3.14,0.,0.));
		sd = smin(sd, -(length(p.xz) - 5.), -1.);

		sd=abs(sd)-0.001;

		if(sd<0.01)
		{	
			io=1.2;
			oc=vec3(0.,0.1,0.2)+abs(space(p))*vec3(0.9,0.,0.1);
			ss=vec3(length(p)*0.1);
			oa=0.1;
			ec=2;	

		}
		return sd;
}

void nm(){mat3 k=mat3(cp,cp,cp)-mat3(.001);cn=normalize(mp(cp)-vec3(mp(k[0]),mp(k[1]),mp(k[2])));cn=normalize(cn);}

float tr(vec3 ro, vec3 rd)
{cd=0.;md=64.;for(tc=1.;tc<256.;tc++){mp(cp=ro+rd*cd);cd+=sd;td+=sd;
if(sd<md&&sd<cd-0.06)md=sd;if(sd<0.0001||cd>64.)break;}nm();return cd;}

void px(vec3 rd)
{
  cc.rgb=vec3(0.1,0.1,0.3)+length(pow(abs(rd+vec3(0,0,0)),vec3(10)))*vec3(0.)+gl/tc;
  if(cd>64.){cc.a=1.;return;}cc.a=oa;
	vec3 ld = -normalize(cp+cn-vec3(0.,10,-30));
  float df=clamp(length(cn*ld),0.,1.),
	sp=(1.-length(cross(rd,cn*ld)))*0.1,
	ao=min(mp(cp+cn*0.3)-0.3,0.3)*0.8;
  vec3 fr=pow(1.-abs(dot(rd,-cn)),3.)*mix(cc.rgb,vec3(0.3),0.9)*0.8;
  cc.rgb=(oc*(df+fr+ss)+fr+sp+ao+gl/tc);tr(cp+cn*0.06,ld);
	float sh=1.-mix(1.-clamp(md/0.06,0.,1.),clamp(dot(cn,-ld),0.,1.),0.1)*(io>0.?oa:1.)*0.;
	cc.rgb=mix(mix(vec3(sh),oc,0.5),cc.rgb,sh)*sh;
}


void render(vec2 frag, vec2 res, float time, out vec4 col)
{
  uv=vec2(frag.x/res.x,frag.y/res.y);
  uv-=0.5;uv/=vec2(res.y/res.x,1);
	tt=mod(time+45.,120.);

  vec3 ro=vec3(0.,0,0),rd=normalize(vec3(uv*0.5,1.));


	for(int i=0;i<4;i++)
  {
		float d=tr(ro,rd);
		ro=cp-cn*(io<0.?-0.01:0.01);
		cr=refract(rd,cn,i%2==0?1./io:io);
		i=io<0.?i+1:i;
    if((length(cr)==0.&&es<=0)||io<0.)
		{cr=reflect(rd,cn);es=(io<0.?es:ec);}
		px(rd); if(max(es,0)%3==0&&d<64.)rd=cr;es--;
		fc=fc+vec4(cc.rgb*cc.a,cc.a)*(1.-fc.a);
		if(fc.a>=1.)break;
  }
  col = fc/fc.a;
  col.rgb = pow(col.rgb, vec3(0.8));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    render(fragCoord.xy,iResolution.xy,iTime,fragColor);
}
