void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 z = ((2.0 * fragCoord - iResolution.xy) / iResolution.y) * 2.;
    float l = 0.0;
    for (l = 0.0; l < 100.0; l += 1.0) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + vec2(sin(iTime / 3.0), cos(iTime / 2.0));
        if(dot(z, z) > 65536.0) break;
    } // Divide iterations by maximum
    fragColor = vec4(vec3(sqrt(l / 100.0) * 1.4) * vec3(0.1, 1.0, 0.8), 1.0);
}
