bool circle(vec2 p, vec2 c, float r){
              float scaleY = iResolution.y / iResolution.x; // для адаптации круга под размер окна
               return (p.x - c.x) * (p.x - c.x) + (p.y   - c.y) * (p.y - c.y) * (scaleY * scaleY) < r*r; 
  }

bool over(vec2 p, vec3 line)
  {
      return p.y * line.y >= p.x * line.x + line.z;
  }

bool under(vec2 p, vec3 line){
       return p.y * line.y <= p.x * line.x + line.z;
}  

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 crd = fragCoord/iResolution.xy;

     float x = crd.x;
     float y = crd.y;

     fragColor = vec4(1.0, 1.0, 1.0, 1.0);


    // head



     if (circle(vec2(x, y), vec2(0.5, 0.5), 0.121))
        fragColor = vec4(1.0, 0.87, 0.76, 1.0);

     if (over(vec2(x, y), vec3(-8.6,1.0, -2.5)) && under(vec2(x,y), vec3(-3.0, 1.5, 0.5)) )
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);

}
