// Frequency goes from 0 to 12khz (left to right), frequency scaling is linear
vec2 uv;

vec3 lerp4(vec3 colors[4], float k)
{
    float k4 = k * 4.0;
    int index2 = int(floor(k4));
    int index1 = max(index2 - 1, 0);
    return mix(colors[index1], colors[index2], mod(k4, 1.0)); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = fragCoord/iResolution.xy;
    float audio = texture(iChannel0, uv).r;
    vec3 col = lerp4(vec3[4](vec3(0,0,0),vec3(0.5,0,1.0),vec3(1.0,0.75,0.0),vec3(1.0,1.0,1.0)), audio);
