void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    if (fragCoord.y < 1.0)
    {
        fragColor.r = texture(iChannel0, vec2(uv.x, 0.0)).r;
        //fragColor.g = max(texture(iChannel1, vec2(uv.x, 0.0)).g, fragColor.r);
        return;
    }
    fragColor.r = texture(iChannel1, uv - vec2(0, 1.0 / iResolution.y)).r;
}
