float Band( float t, float start, float end, float blur){
    float band1 = smoothstep( start-blur, start+blur, t);
    float band2 = smoothstep( end+blur, end-blur, t);

    return band1*band2;


}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.);

    uv -= .5;

    float mask = 0.;

    mask = Band(uv.x, -.2, .2, 0.005);

    col = vec3(1.,1.,1.)*mask;

    fragColor = vec4(col,1.0);
}
