float inCircle(vec2 uv, float r, vec2 pos, float blur){
    float d = distance(uv, pos);
    return 1.-smoothstep(r-blur, r, d);
}

vec3 drawCircle(vec2 uv, vec3 backgroundColor, vec3 color, float r, vec2 pos, float blur){
    float cf = inCircle(uv,r,pos,blur);
    vec3 ccol = cf * color;
    return mix(backgroundColor, mix(backgroundColor + ccol, ccol, cf), cf);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2. * (fragCoord.xy - 0.5 * iResolution.xy)/min(iResolution.x, iResolution.y);

    vec3 bgcolor = vec3(1.000,0.984,0.228);
    vec3 col = vec3(0.000,0.608,1.000);
    float r = 0.448;
    float blur = 0.282;
    vec2 pos = vec2(-0.060,0.090);
    vec3 color = drawCircle(uv, bgcolor, col, r, pos, blur);

    fragColor = vec4(color,1.0);
}
