#define bit(p,v)   int( (v) * float(1<<23) ) >> p & 1

void mainImage( out vec4 O, vec2 U )
{
    bool right = U.y > iResolution.y/2.;
    int p = int(U)/16;
    O = vec4( bit( p,  p < 24 ? fract(right ? iTime : iDate.w )
                              : floor(right ? iTime : iDate.w )
            )    );
    if (int(U)%16==0) O = vec4(0,1,0,1);
}
