    bool Ellipse(vec2 p1, vec2 p2, float r, float a, float b){
        float sc = iResolution.y / iResolution.x;
         return (p1.x-p2.x)*(p1.x-p2.x)*0.5/(a*a) + (p1.y-p2.y)*(p1.y-p2.y)*0.5*(sc*sc)/(b*b) < r*r; 
    }

    bool Circle(vec2 p1, vec2 p2, float r){
        float sc = iResolution.y / iResolution.x;
         return (p1.x-p2.x)*(p1.x-p2.x) + (p1.y-p2.y)*(p1.y-p2.y)*(sc*sc) < r*r; 
    }

    float Checkup(vec2 p1, vec2 p2, vec2 p3){
        return (p3.x-p1.x)*(p2.y-p1.y) - (p3.y-p1.y)*(p2.x-p1.x);
    }
          bool OneLine(vec2 p1, vec2 p2, vec2 p3, vec2 p4) {
             return Checkup(p1, p2, p3)*Checkup(p1, p2, p4) >= 0.;
          }
              bool Triangle(vec2 p1, vec2 p2, vec2 p3, vec2 p4){
                       return OneLine(p1, p2, p3, p4) && OneLine(p2, p3, p1, p4) && OneLine(p3, p1, p2, p4);
              }

