#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

        float s1 = .75;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 36.*4.*2.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = c1.z;
    for(int i=0;i<6;i++)
    {
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 0; k < 3; k++){
            uv += (t2.yx);
            uv /= -scale;
            t2 = triangle_wave(uv.yx-1.5,1.5);
            t3 = -triangle_wave(uv-1.,1.5);
            uv.yx = (t2+t3);
        }
        col.x = 1.-min(uv.y+uv.x+col.x,col.x)*s1;
        col = abs(col.yzx-vec3(1.5-col.x))/s1;
    }
    fragColor = vec4(col*s1,1.0);
}
