uint hash(uint seed){
    seed = (seed ^ 61u) ^ (seed >> 16u);
    seed *= 9u;
    seed = seed ^ (seed >> 4u);
    seed *= 0x27d4eb2du;
    seed = seed ^ (seed >> 15u);
    return seed;
}
uint combineSeed(uint a, uint b){
	return hash(a) ^ hash(b);
}
float rand(inout uint seed){
	seed = hash(seed);
	return float(seed) * (1.0 / float(~0u));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uint seed = combineSeed(combineSeed(uint(iTime*1000.), uint(fragCoord.x)), uint(fragCoord.y));

    // Output to screen
    fragColor = vec4(rand(seed), rand(seed), rand(seed), 1.0);
}
