void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = -2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;

    // Pixel color
    vec3 col = vec3(1.0);
    vec3 base = vec3(1.0,0.8,0.5);
    vec3 rose = vec3(1.0,0.5,0.5);
    vec3 black = vec3(0.0,0.0,0.0);
    vec3 red = vec3(1.0,0.0,0.0);
    vec3 white = vec3(1.0,1.0,1.0);
    vec3 blue = vec3(0.0,0.5,1.0);

    // head
    float   x = uv.x + 1.25;
    float   y = uv.y;
    float   r = 0.4;
     if (x > y*15.0 && -x < y*15.0 ) {
         col = base;
     }

    x = uv.x;
    y = uv.y;
     if (x > 0.0) {
         col = vec3(1.0,1.0,1.0);
     }

    x = uv.x;
    y = uv.y;
     if (x*x + y*y < r*r) {
         col = base;
     } 

    x = uv.x + 0.05;
    y = uv.y - 0.15;
    r = 0.15;
     if (x*x + y*y < r*r) {
         col = rose;
     }

    x = uv.x + 0.25;
    y = uv.y - 0.25;
    r = 0.05;
     if (x*x + y*y < r*r) {
         col = black;
     }

    x = uv.x + 0.26;
    y = uv.y - 0.24;
    r = 0.05;
     if (x*x + y*y < r*r) {
         col = base;
     } 

    x = uv.x + 0.1;
    y = uv.y + 0.1;
    r = 0.1;
     if (x*x + y*y < r*r) {
         col = black;
     }

    x = uv.x + 0.09;
    y = uv.y + 0.12;
    r = 0.1;
     if (x*x + y*y < r*r) {
         col = base;
     }

    x = uv.x + 0.09;
    y = uv.y - 0.01;
     if (abs(x) < 0.01 && abs(y) < 0.03) {
         col = blue;
     }

    x = uv.x + 0.09;
    y = uv.y - 0.05;
    r = 0.025;
     if (x*x + y*y < r*r) {
         col = blue;
     } 

    // hat
    x = uv.x;
    y = uv.y + 0.35;
     if (abs(x) < 0.3 && abs(y) < 0.05) {
         col = red;
     }

    x = uv.x + 0.3;
    y = uv.y + 0.35;
    r = 0.05;
     if (x*x + y*y < r*r) {
         col = red;
     }

    x = uv.x - 0.3;
    y = uv.y + 0.35;
    r = 0.05;
     if (x*x + y*y < r*r) {
         col = red;
     }

    x = uv.x;
    y = uv.y + 0.6;
     if (abs(x) < 0.27 && abs(y) < 0.3) {
         col = red;
     } 

    x = uv.x - 0.4;
    y = uv.y + 0.785;
    r = 0.4;
     if (x*x + y*y < r*r) {
         col = white;
     } 

    x = uv.x + 0.4;
    y = uv.y + 0.785;
    r = 0.4;
     if (x*x + y*y < r*r) {
         col = white;
     } 

    x = uv.x ;
    y = uv.y + 0.8;
    r = 0.1;
     if (x*x + y*y < r*r) {
         col = red;
     } 

    // body
    x = uv.x;
    y = uv.y - 0.55;
     if (abs(x) < 0.1 && abs(y) < 0.2) {
         col = base;
     }

    x = uv.x;
    y = uv.y - 0.95;
    r = 0.3;
     if (x*x + y*y/2.0 < r*r) {
         col = red;
     }

    x = uv.x + 0.27;
    y = uv.y - 0.8;
    r = 0.06;
     if (x*x + y*y < r*r) {
         col = black;
     } 

    x = uv.x + 0.27;
    y = uv.y - 0.8;
    r = 0.05;
     if (x*x + y*y < r*r) {
         col = white;
     } 

    x = uv.x + 0.2;
    y = uv.y - 0.65;
    r = 0.06;
     if (x*x + y*y < r*r) {
         col = black;
     } 

    x = uv.x + 0.2;
    y = uv.y - 0.65;
    r = 0.05;
     if (x*x + y*y < r*r) {
         col = white;
     }

    x = uv.x + 0.3;
    y = uv.y - 0.95;
    r = 0.06;
     if (x*x + y*y < r*r) {
         col = black;
     } 

    x = uv.x + 0.3;
    y = uv.y - 0.95;
    r = 0.05;
     if (x*x + y*y < r*r) {
         col = white;
     } 

    // Output to screen
    fragColor = vec4(col,1.0);
}
