    float make_circle(vec2 coord, vec2 pos, float rad, float eps){
                     float dist = length(coord - pos);
                      return smoothstep(rad, rad - eps, dist); /*
                     Использование smoothstep обусловлено 
                     необходимостью избавиться от блюра на 
                     границе круга
                                                              */
    }



