void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*vec3(
            sin(cos(uv.x * 45.0) * cos(uv.y * 45.0) * 3.14 +
            cos(iTime*1.13 + uv.x * 170.0 + 2.0) * 0.3 +
            cos(iTime*0.9 + uv.y * 180.0 + 2.0) * 0.26
            ), 
            sin(cos(uv.x * 45.0) * cos(uv.y * 45.0) * 3.14 +
            cos(iTime*0.23 + uv.x * 162.0 + 2.0) * 0.13 +
            cos(iTime*-1.9 + uv.y * 185.0 + 2.0) * 0.21
            ), 
            sin(cos(uv.x * 45.0) * cos(uv.y * 45.0) * 3.14 +
            cos(iTime*-1.83 + uv.x * 152.0 + 2.0) * 0.73 +
            cos(iTime*2.9 + uv.y * 195.0 + 2.0) * 0.29
            ));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
