// based on https://github.com/andrewlowndes/perfect-antialiasing
const float EPSILON = 0.0001;

vec4 sort(vec4 val) {
  float a = min(val.x, val.y);
  float b = max(val.x, val.y);
  float c = min(val.z, val.w);
  float d = max(val.z, val.w);

  float h = max(a, min(b, c));
  float i = min(d, max(b, c));

  return vec4(min(a, c), min(h, i), max(h, i), max(b, d));
}

float timeAtPos(float startPos, float dir, float newPos) {
  return abs(dir) < EPSILON ? 0.0 : clamp((newPos - startPos) / dir, 0.0, 1.0);
}

int det2(ivec2 p1, ivec2 p2) {
  return (p1.x * p2.y) - (p1.y * p2.x);
}

const float precision_scale = 1000.0;
const float two_precision_scale_squared = 2.0 * precision_scale * precision_scale;

ivec2 prepPoint(vec2 point, vec2 pixelMin, vec2 pixelMax) {
    //limit the precision by scaling the point to map to an int
    vec2 constrained_point = clamp(point, pixelMin, pixelMax);
    return ivec2(floor(constrained_point * precision_scale));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //triangle coords
    vec2 a = floor((vec2(0.1, 0.2) + vec2(sin(iTime), cos(iTime)) * 0.2) * iResolution.xy);
    vec2 b = floor(vec2(0.8, 0.1) * iResolution.xy);
    vec2 c = floor(length(iMouse.xy) > 10.0 ? iMouse.xy : vec2(0.5, 0.5) * iResolution.xy);

    vec2 pixelMin = floor(fragCoord.xy);
    vec2 pixelMax = pixelMin + 1.0;

    //intersect the triangle with the pixel bounds
    vec2 e1 = b - a;
    vec2 e2 = c - b;
    vec2 e3 = a - c;

    vec4 firstLineTs = sort(vec4(
        timeAtPos(a.x, e1.x, pixelMin.x),
        timeAtPos(a.x, e1.x, pixelMax.x),
        timeAtPos(a.y, e1.y, pixelMin.y),
        timeAtPos(a.y, e1.y, pixelMax.y)
    ));

    ivec2 p1 = prepPoint(a + e1 * firstLineTs.x, pixelMin, pixelMax);
    ivec2 p2 = prepPoint(a + e1 * firstLineTs.y, pixelMin, pixelMax);
    ivec2 p3 = prepPoint(a + e1 * firstLineTs.z, pixelMin, pixelMax);
    ivec2 p4 = prepPoint(a + e1 * firstLineTs.w, pixelMin, pixelMax);

    vec4 secondLineTs = sort(vec4(
        timeAtPos(b.x, e2.x, pixelMin.x),
        timeAtPos(b.x, e2.x, pixelMax.x),
        timeAtPos(b.y, e2.y, pixelMin.y),
        timeAtPos(b.y, e2.y, pixelMax.y)
    ));

    ivec2 p5 = prepPoint(b + e2 * secondLineTs.x, pixelMin, pixelMax);
    ivec2 p6 = prepPoint(b + e2 * secondLineTs.y, pixelMin, pixelMax);
    ivec2 p7 = prepPoint(b + e2 * secondLineTs.z, pixelMin, pixelMax);
    ivec2 p8 = prepPoint(b + e2 * secondLineTs.w, pixelMin, pixelMax);

    vec4 thirdLineTs = sort(vec4(
        timeAtPos(c.x, e3.x, pixelMin.x),
        timeAtPos(c.x, e3.x, pixelMax.x),
        timeAtPos(c.y, e3.y, pixelMin.y),
        timeAtPos(c.y, e3.y, pixelMax.y)
    ));

    ivec2 p9  = prepPoint(c + e3 * thirdLineTs.x, pixelMin, pixelMax);
    ivec2 p10 = prepPoint(c + e3 * thirdLineTs.y, pixelMin, pixelMax);
    ivec2 p11 = prepPoint(c + e3 * thirdLineTs.z, pixelMin, pixelMax);
    ivec2 p12 = prepPoint(c + e3 * thirdLineTs.w, pixelMin, pixelMax);

    //compute the area under the formed polygon
    int polygonArea = (
        det2(p1, p2) + det2(p2, p3) + det2(p3, p4) + det2(p4, p5) +
        det2(p5, p6) + det2(p6, p7) + det2(p7, p8) + det2(p8, p9) + 
        det2(p9, p10) + det2(p10, p11) + det2(p11, p12) + det2(p12, p1)
    );

    float contribution = clamp(float(polygonArea) / two_precision_scale_squared, 0.0, 1.0);
    fragColor = vec4(contribution, 0.0, 0.0, 1.0);
}
