void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;                         // normalized coordinates

    O = texture(iChannel0, U); O*=O;             // texture (flat space)
# if 1                                           // boost chrominance    
    vec4  M = texture(iChannel0, U, 99.); M*=M;
    float w = min(M.r, min(M.g,M.b)); 
    O = (O-w)/(1.-w);
# endif 

    if (iFrame%4 > 0 ) 
    // O = vec4( dot(O, vec4(.21,.72,.07,0) ) ); // luminance (sRGB)
    // O = vec4(O.r+O.g+O.b)/3.;                 // ( approx versions cause flickering )
       O = vec4( dot(O, vec4(.3,.59,.11,0) ) );  // luminance (flat space)

    O = sqrt(O);                                 // to sRGB
}
