#define PI 3.14159

#define INVERSE

vec2 hash2(in vec2 uv) {
    vec2 n = vec2(
        fract(sin(uv.x * 34.0123 + uv.y * 3.7712) * 99.7803),
        fract(sin(uv.y * 48.1333 - uv.x * 19.571) * 64.1601)
    );
    float r = n.x * PI * 2.0;
    float l = pow(n.y, 0.5);
    return vec2(cos(r) * l, sin(r) * l);
}

float vnoise(in vec2 uv, in float size, in float thin) {
    vec3 e = vec3(0.0, 1.0, -1.0);
    float d = 10.0;
    vec2 v00 = e.xx + 0.5 + hash2(floor(uv / size) + e.xx) * 0.5;
    vec2 v01 = e.xy + 0.5 + hash2(floor(uv / size) + e.xy) * 0.5;
    vec2 v11 = e.yy + 0.5 + hash2(floor(uv / size) + e.yy) * 0.5;
    vec2 v10 = e.yx + 0.5 + hash2(floor(uv / size) + e.yx) * 0.5;
    vec2 v1b = e.yz + 0.5 + hash2(floor(uv / size) + e.yz) * 0.5;
    vec2 v0b = e.xz + 0.5 + hash2(floor(uv / size) + e.xz) * 0.5;
    vec2 vbb = e.zz + 0.5 + hash2(floor(uv / size) + e.zz) * 0.5;
    vec2 vb0 = e.zx + 0.5 + hash2(floor(uv / size) + e.zx) * 0.5;
    vec2 vb1 = e.zy + 0.5 + hash2(floor(uv / size) + e.zy) * 0.5;
    vec2 k = fract(uv / size);
    d = min(d, distance(k, v00));
    d = min(d, distance(k, v01));
    d = min(d, distance(k, v11));
    d = min(d, distance(k, v10));
    d = min(d, distance(k, v1b));
    d = min(d, distance(k, v0b));
    d = min(d, distance(k, vbb));
    d = min(d, distance(k, vb0));
    d = min(d, distance(k, vb1));
#ifdef INVERSE
    return 1.0 - (pow(d, thin));
#endif
    return pow(d, thin);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(1.0);

    col *= vnoise(fragCoord.xy + 64.0 * vec2(cos(iTime * 1.40), sin(iTime * 2.01)), 32.0, 1.7);

    fragColor = vec4(col,1.0);
}
