void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // center screen coords  
    vec2 uv = (fragCoord.xy- (0.5*iResolution.xy))/iResolution.y;

    vec2 subject = uv * 8. *  (sin(iTime * 0.5)+2.); //some arbitrary motion

    // if you get around 144 fps use a higher repeat value
    int repeat = 1; //10 000 is enough for RTX2070
    float test;
    for(int i=0; i<repeat; i++)
    {
        vec2 a = subject + float(i); //permutation each iteration

        // use which ever you wish to test
        float x = dot(a,a); //95fps
        //float x = length(a); //60fps

        test += x; // each iteration adds to final image
    }

    // Output to screen
    fragColor = vec4(fract(test));
}
