vec2 rand2(vec2 co)
{
    vec3 a = fract(co.xyx*vec3(123.4, 234.34, 345.65));
    a += dot(a, a+34.45);
    return fract(vec2(a.x*a.y, a.y *a.z));
}

mat2 r2D(float a){ return mat2(cos(a), -sin(a), sin(a), cos(a)); }

//iq's 2D line sdg
float sdgSegment( in vec2 p, in vec2 a, in vec2 b, in float r )
{
    vec2 ba = b-a, pa = p-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    vec2  q = pa-h*ba;
    float d = length(q);
    return smoothstep(r, r+0.025, d);
}

#define PI 3.145962
#define GS 8.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 gv = fract(uv*GS)-0.5;

    vec2 rand = rand2(floor(uv*GS));

    float len = 0.5;
    float r = 0.02 + fract(length(gv)*0.1);
    float line = sdgSegment(gv*r2D(PI/(2.0)*floor(rand.x+0.5 + cos(iTime*0.1)*0.5+0.5)), vec2(-len), vec2(len), r);

    vec3 col = vec3(1.0 - line);
    fragColor = vec4(col,1.0);
}
